export const Suggester: React.FC<any> = (): ReactElement => {
  const {t} = useTranslation()
  const classes: any = useStyles({})
  const dispatch = useDispatch()
  const terms = getTerms()
  const [selectedItem, setSelectedItem] = React.useState([])
  const [inputValue, setInputValue] = React.useState('')

  const onInputChange = (e): void => {
    setInputValue(e.target.value)
    dispatch(setSuggest({stringInput: e.target.value, suggest: true}))
  }

  const onSelect = (item): void => {
    let newSelectedItem = [...selectedItem]
    if (newSelectedItem.indexOf(item) === -1) {
      newSelectedItem = [...newSelectedItem, item]
    }
    setInputValue('')
    setSelectedItem(newSelectedItem)
    const quotedItem = `"${item}"`
    dispatch(setSuggest({stringInput: quotedItem, suggest: true}))
    dispatch(setStart({start: 0}))
  }

  const handleDelete = (item): any => (): void => {
    const newSelectedItem = [...selectedItem]
    newSelectedItem.splice(newSelectedItem.indexOf(item), 1)
    setSelectedItem(newSelectedItem)
    dispatch(setSuggest({stringInput: '', suggest: true}))
    dispatch(setStart({start: 0}))
  }

  const handleKeyDown = (event): void => {
    if (selectedItem.length && !inputValue.length && event.key === 'Backspace') {
      setSelectedItem(selectedItem.slice(0, selectedItem.length - 1))
    }
  }

  const getSuggestions = (value, { showEmpty = false } = {}): string[] => {
    const inputValue = deburr(value.trim()).toLowerCase()
    const inputLength = inputValue.length
    let count = 0
    const suggestions = terms && terms.filter((t): boolean => {
      const keep = count < 5 && t.slice(0, inputLength).toLowerCase() === inputValue
      if (keep) {
        count += 1
      }
      return keep
    })
    return inputLength === 0 && !showEmpty
      ? []
      : suggestions
  }

  return (
    <div className={classes.root}>
      <Downshift
        id="downshift-simple"
        inputValue={inputValue}
        onChange={onSelect}>
        {({
          getInputProps,
          getItemProps,
          getMenuProps,
          highlightedIndex,
          isOpen,
          inputValue: inputValue2,
          selectedItem: selectedItem2,
        }): ReactElement => (
          <div className={classes.container}>
            {renderInput({
              classes,
              InputProps: getInputProps({
                onChange: onInputChange,
                onKeyDown: handleKeyDown,
                placeholder: t('suggest'),
                startAdornment: selectedItem.map((item): ReactElement => (
                  <Chip
                    classes={{
                      label: classes.label,
                      root: classes.chip
                    }}
                    component='div'
                    key={item}
                    label={item}
                    onDelete={handleDelete(item)}
                    tabIndex={-1}
                  />
                )),
              }),
            })}
            <div {...getMenuProps()}>
              {isOpen ? (
                <Paper
                  className={classes.paper}
                  square
                >
                  {terms && getSuggestions(inputValue2).map((suggestion, index): ReactElement =>
                    renderSuggestion({
                      highlightedIndex,
                      index,
                      itemProps: getItemProps({ item: suggestion }),
                      selectedItem: selectedItem2,
                      suggestion,
                    }),
                  )}
                </Paper>
              ) : null}
            </div>
          </div>
        )}
      </Downshift>
    </div>
  )
}

import {AppBar, Tab, Tabs, Theme, makeStyles, } from '@material-ui/core'
import React, {ReactElement} from 'react'
import {
  getDocTypes,
  getStringInput,
  setIsViewExpanded,
  setSelectedFilters,
  setStart,
  setSuggest
} from "@react-discovery/solr"
import {useDispatch} from "react-redux"

const useStyles = makeStyles((theme: Theme): any => ({
  root: {
    backgroundColor: theme.palette.background.paper,
    width: '100%',
  },
}))

export const TabsAppBar: React.FC<any> = (): ReactElement => {
  const classes: any = useStyles({})
  const dispatch = useDispatch()
  const docTypes = getDocTypes()
  const stringInput = getStringInput()
  const [value, setValue] = React.useState(0)

  const TYPE_FIELD = 'type_s'

  const handleChange = ({}: React.ChangeEvent<{}>, newValue: number): void => {
    const typeString = docTypes[newValue]
    const newFilters = Array.of(typeString)
    dispatch(setSuggest({stringInput, suggest: false}))
    dispatch(setSelectedFilters({field: TYPE_FIELD, filters: newFilters}))
    dispatch(setIsViewExpanded({isViewExpanded: false}))
    dispatch(setStart({start: 0}))
    setValue(newValue)
  }
  const buildTabs = (): ReactElement[] => {
    return docTypes.map((ft, i): ReactElement => <Tab href='' key={i} label={ft} />)
  }

  return docTypes ? (
    <div className={classes.root}>
      <AppBar color="default" position="static">
        <Tabs
          centered
          indicatorColor="primary"
          onChange={handleChange}
          textColor="primary"
          value={value}
        >
          {buildTabs()}
        </Tabs>
      </AppBar>
    </div>
  ) : null
}

